// AttendanceHome.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Scanner } from '@yudiel/react-qr-scanner';
import './AttendanceHome.css';
import AdminLoginModal from './../common/AdminLoginModal';
import SuccessModal from './../common/SuccessModal';
import ErrorModal from './../common/ErrorModal';

// Variables de entorno o constantes (usar mejor variables de entorno en producción)
const ADMIN_USER = 'admin';
const ADMIN_PASS = '1234';

const AttendanceHome = () => {
  const navigate = useNavigate();

  // Estado principal para el registro de asistencia (ID de empleado)
  const [employeeId, setEmployeeId] = useState('');

  // Estado del modal de administrador
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Estado del modal de éxito
  const [showSuccess, setShowSuccess] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  // Estado del modal de error
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  // Estado para controlar el escáner QR
  const [isScannerActive, setIsScannerActive] = useState(false);
  const [scanError, setScanError] = useState('');

  // Función unificada para mostrar el modal de éxito
  const showSuccessModal = (id) => {
    setSuccessMessage(`Asistencia registrada para ID: ${id}`);
    setShowSuccess(true);
  };

  // Función para cerrar el modal de éxito
  const handleCloseSuccess = () => {
    setShowSuccess(false);
    setSuccessMessage('');
  };

  // Función para mostrar el modal de error
  const showErrorModal = (message) => {
    setErrorMessage(message);
    setShowError(true);
  };

  // Función para cerrar el modal de error
  const handleCloseError = () => {
    setShowError(false);
    setErrorMessage('');
  };

  // Función para registrar asistencia manual (por input)
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!employeeId.trim()) {
      showErrorModal('Por favor ingrese un ID válido');
      return;
    }

    console.log(`Asistencia registrada para ID: ${employeeId}`);

    // Mostrar modal de éxito con el ID
    showSuccessModal(employeeId);

    // Limpiar campo
    setEmployeeId('');
  };

  // Función para manejar el escaneo exitoso del QR
  const handleQrScan = (result) => {
    if (result && result.length > 0) {
      const scannedData = result[0].rawValue;
      console.log(`QR escaneado: ${scannedData}`);

      // Desactivar el escáner
      setIsScannerActive(false);

      // Mostrar modal de éxito con el ID escaneado
      showSuccessModal(scannedData);

      // Llenar el input con el ID escaneado (opcional)
      setEmployeeId(scannedData);
    }
  };

  // Función para manejar errores del escáner
  const handleScanError = (error) => {
    console.error('Error en el escáner:', error);
    setScanError('Error al acceder a la cámara');
  };

  // Toggle del escáner
  const toggleScanner = () => {
    setIsScannerActive(!isScannerActive);
    setScanError('');
  };

  // Abrir/cerrar modal de administrador
  const toggleAdminModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  // Lógica de autenticación del administrador
  const handleAdminLogin = (username, password) => {
    if (username === ADMIN_USER && password === ADMIN_PASS) {
      console.log('¡Acceso de Administrador Concedido!');
      setIsModalOpen(false);
      navigate('/employees'); // Redirige a lista de empleados
      return true;
    } else {
      console.log('Error: Nombre de usuario o contraseña incorrectos.');
      // Mostrar modal de error en lugar de alert
      setIsModalOpen(false);
      showErrorModal('Credenciales no identificadas');
      return false;
    }
  };

  return (
    <div className="attendance-home">
      <div className="attendance-container">
        {/* Lado izquierdo - Formulario */}
        <div className="attendance-left">
          <h2 className="attendance-title">Registro de Asistencia</h2>

          <form className="attendance-form" onSubmit={handleSubmit}>
            <input
              type="text"
              className="attendance-input"
              placeholder="Ingrese su ID"
              value={employeeId}
              onChange={(e) => setEmployeeId(e.target.value)}
              required
            />

            <button type="submit" className="attendance-button">
              INGRESAR
            </button>
          </form>

          {/* Ícono para abrir el modal de administrador - Usuario con Escudo */}
          <div 
            className="attendance-settings-icon" 
            onClick={toggleAdminModal}
            title="Acceso Administrador"
          >
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
              <path d="M12,1L3,5V11C3,16.55 6.84,21.74 12,23C17.16,21.74 21,16.55 21,11V5L12,1M12,5A3,3 0 0,1 15,8A3,3 0 0,1 12,11A3,3 0 0,1 9,8A3,3 0 0,1 12,5M17.13,17C15.92,18.85 14.11,20.24 12,20.92C9.89,20.24 8.08,18.85 6.87,17C6.53,16.5 6.24,16 6,15.47C6,13.82 8.71,12.47 12,12.47C15.29,12.47 18,13.79 18,15.47C17.76,16 17.47,16.5 17.13,17Z" />
            </svg>
          </div>
        </div>

        {/* Lado derecho - Escáner QR */}
        <div className="attendance-right">
          <div className="qr-title-fixed">ESCANEA CÓDIGO QR</div>

          <div className="qr-scanner">
            {!isScannerActive ? (
              // Vista del frame decorativo cuando no está escaneando
              <div className="scanner-frame" onClick={toggleScanner}>
                <div className="scanner-corner scanner-tl"></div>
                <div className="scanner-corner scanner-tr"></div>
                <div className="scanner-corner scanner-bl"></div>
                <div className="scanner-corner scanner-br"></div>
                <div className="scanner-dot"></div>
                <div className="scanner-text">
                  Toca para escanear
                </div>
              </div>
            ) : (
              // Vista de la cámara activa
              <div className="scanner-camera-container">
                <Scanner
                  onScan={handleQrScan}
                  onError={handleScanError}
                  constraints={{
                    facingMode: 'environment',
                  }}
                  components={{
                    finder: false,
                  }}
                  styles={{
                    container: {
                      width: '100%',
                      height: '100%',
                      borderRadius: '1.25rem',
                      overflow: 'hidden',
                    },
                    video: {
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                    }
                  }}
                />

                <div className="scanner-overlay">
                  <div className="scanner-corner scanner-tl"></div>
                  <div className="scanner-corner scanner-tr"></div>
                  <div className="scanner-corner scanner-bl"></div>
                  <div className="scanner-corner scanner-br"></div>
                  <div className="scanner-grid"></div>
                  <div className="scanner-line"></div>
                  <div className="scanner-status-text">Escaneando...</div>
                </div>

                <button 
                  className="scanner-close-btn"
                  onClick={toggleScanner}
                >
                  ✕
                </button>

                {scanError && (
                  <div className="scanner-error">{scanError}</div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modal de administrador */}
      {isModalOpen && (
        <AdminLoginModal
          isOpen={isModalOpen}
          onClose={toggleAdminModal}
          onLogin={handleAdminLogin}
        />
      )}

      {/* Modal de éxito */}
      <SuccessModal
        isOpen={showSuccess}
        onClose={handleCloseSuccess}
        message={successMessage}
        autoCloseDelay={3000}
      />

      {/* Modal de error */}
      <ErrorModal
        isOpen={showError}
        onClose={handleCloseError}
        message={errorMessage}
        autoCloseDelay={3000}
      />
    </div>
  );
};

export default AttendanceHome;
