import React, { useState } from "react";
import "./ReportsHome.css";

const ReportsHome = () => {
  // -----------------------------
  // ESTADOS
  // -----------------------------
  const [params, setParams] = useState({
    fechaInicio: "",
    fechaFin: "",
    horaEntrada: "",
    horaSalida: "",
    horasTrabajadas: "",
    busqueda: "",
  });

  const [historial, setHistorial] = useState([]);

  // -----------------------------
  // MANEJO DE INPUTS
  // -----------------------------
  const handleChange = (e) => {
    setParams({
      ...params,
      [e.target.name]: e.target.value,
    });
  };

  // -----------------------------
  // GENERAR REPORTE
  // -----------------------------
  const handleGenerarReporte = () => {
    if (!params.fechaInicio || !params.fechaFin) {
      alert("Debes elegir la fecha de inicio y fin.");
      return;
    }

    const nuevoReporte = {
      id: historial.length + 1,
      nombre: `Reporte #${historial.length + 1}`,
      fechaGenerado: new Date().toLocaleString(),
      parametros: { ...params },
    };

    setHistorial([nuevoReporte, ...historial]);
  };

  // -----------------------------
  // VER REPORTE (SIMULADO)
  // -----------------------------
  const handleVerReporte = (reporte) => {
    alert(
      "Vista previa del reporte:\n\n" +
        JSON.stringify(reporte.parametros, null, 2)
    );
  };

  // -----------------------------
  // DESCARGAR REPORTE (SIMULADO)
  // -----------------------------
  const handleDescargarReporte = (reporte) => {
    alert("Descargando archivo de: " + reporte.nombre);
  };

  return (
    <div className="reports-home">
      <h2>Reportes e Historial</h2>

      <div className="reports-container">
        {/* ----------------------------- */}
        {/* PANEL IZQUIERDO: FILTROS */}
        {/* ----------------------------- */}
        <div className="filters-section">
          <h3>Filtros</h3>

          <div className="filter-group">
            <label>Fecha de inicio:</label>
            <input
              type="date"
              name="fechaInicio"
              value={params.fechaInicio}
              onChange={handleChange}
            />
          </div>

          <div className="filter-group">
            <label>Fecha de fin:</label>
            <input
              type="date"
              name="fechaFin"
              value={params.fechaFin}
              onChange={handleChange}
            />
          </div>

          <div className="filter-group">
            <label>Hora de entrada:</label>
            <input
              type="time"
              name="horaEntrada"
              value={params.horaEntrada}
              onChange={handleChange}
            />
          </div>

          <div className="filter-group">
            <label>Hora de salida:</label>
            <input
              type="time"
              name="horaSalida"
              value={params.horaSalida}
              onChange={handleChange}
            />
          </div>

          <div className="filter-group">
            <label>Horas trabajadas:</label>
            <input
              type="number"
              name="horasTrabajadas"
              value={params.horasTrabajadas}
              onChange={handleChange}
            />
          </div>

          <div className="filter-group">
            <label>B√∫squeda (opcional):</label>
            <input
              type="text"
              name="busqueda"
              value={params.busqueda}
              onChange={handleChange}
              placeholder="Buscar por nombre, folio, etc."
            />
          </div>

          <button className="generate-button" onClick={handleGenerarReporte}>
            GENERAR REPORTE
          </button>
        </div>

        {/* ----------------------------- */}
        {/* PANEL CENTRAL: PAR√ÅMETROS */}
        {/* ----------------------------- */}
        <div className="reports-content">
          <h3>Par√°metros Seleccionados</h3>

          {!params.fechaInicio && !params.fechaFin ? (
            <p>Seleccione los filtros para ver los par√°metros aqu√≠.</p>
          ) : (
            <div className="params-preview">
              <p><strong>Fecha inicio:</strong> {params.fechaInicio}</p>
              <p><strong>Fecha fin:</strong> {params.fechaFin}</p>
              <p><strong>Hora entrada:</strong> {params.horaEntrada}</p>
              <p><strong>Hora salida:</strong> {params.horaSalida}</p>
              <p><strong>Horas trabajadas:</strong> {params.horasTrabajadas}</p>
              <p><strong>B√∫squeda:</strong> {params.busqueda || "N/A"}</p>
            </div>
          )}
        </div>

        {/* ----------------------------- */}
        {/* PANEL DERECHO: HISTORIAL */}
        {/* ----------------------------- */}
        <div className="history-section">
          <h3>Historial de Reportes</h3>

          {historial.length === 0 ? (
            <p>No hay reportes generados a√∫n.</p>
          ) : (
            <table className="history-table">
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Fecha generado</th>
                  <th>Ver</th>
                  <th>Descargar</th>
                </tr>
              </thead>

              <tbody>
                {historial.map((rep) => (
                  <tr key={rep.id}>
                    <td>{rep.nombre}</td>
                    <td>{rep.fechaGenerado}</td>
                    <td>
                      <button onClick={() => handleVerReporte(rep)}>
                        üëÅ Ver
                      </button>
                    </td>
                    <td>
                      <button onClick={() => handleDescargarReporte(rep)}>
                        ‚¨á Descargar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportsHome;
