import React, { useState } from 'react';
import './EmployeeList.css';
import SearchBar from './SearchBar';
import EmployeeTable from './EmployeeTable';
import EmployeeForm from './EmployeeForm';
import DeleteConfirmation from './DeleteConfirmation';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([
    { id: 'EMP001', name: 'Ana Martínez', area: 'Ciber-escuela', role: 'Docente', active: true, schedule: 'Lun-Vie 8:00-14:00' },
    { id: 'EMP002', name: 'Carlos Gómez', area: 'Cultura', role: 'Coordinador', active: true, schedule: 'Lun-Vie 9:00-15:00' },
    { id: 'EMP003', name: 'Lucía Fernández', area: 'Deporte', role: 'Auxiliar', active: false, schedule: 'Lun-Vie 8:00-14:00' },
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [employeeToDelete, setEmployeeToDelete] = useState(null);

  const handleAddNew = () => {
    setSelectedEmployee(null);
    setShowForm(true);
  };

  const handleEdit = (employee) => {
    setSelectedEmployee(employee);
    setShowForm(true);
  };

  const handleDelete = (employee) => {
    setEmployeeToDelete(employee);
    setShowDeleteConfirm(true);
  };

  const handleSaveEmployee = (employeeData) => {
    if (selectedEmployee) {
      // Editar existente
      setEmployees(employees.map(emp => 
        emp.id === selectedEmployee.id ? { ...employeeData, id: selectedEmployee.id } : emp
      ));
    } else {
      // Agregar nuevo
      const newId = `EMP${String(employees.length + 1).padStart(3, '0')}`;
      setEmployees([...employees, { ...employeeData, id: newId }]);
    }
    setShowForm(false);
  };

  const confirmDelete = () => {
    setEmployees(employees.filter(emp => emp.id !== employeeToDelete.id));
    setShowDeleteConfirm(false);
    setEmployeeToDelete(null);
  };

  const filteredEmployees = employees.filter(emp =>
    emp.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.role.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="employee-list">
      <div className="employee-header">
        <h2>Gestión de Empleados</h2>
        <p className="employee-stats">
          {employees.length} empleados • 
          {employees.filter(e => e.active).length} activos
        </p>
      </div>

      <SearchBar
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        onAddNew={handleAddNew}
      />

      <EmployeeTable
        employees={filteredEmployees}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {showForm && (
        <EmployeeForm
          isOpen={showForm}
          onClose={() => setShowForm(false)}
          onSave={handleSaveEmployee}
          employee={selectedEmployee}
        />
      )}

      {showDeleteConfirm && (
        <DeleteConfirmation
          isOpen={showDeleteConfirm}
          onClose={() => setShowDeleteConfirm(false)}
          onConfirm={confirmDelete}
          employee={employeeToDelete}
        />
      )}
    </div>
  );
};

export default EmployeeList;
