import React, { useState } from 'react';
import './ModalAdmin.css';

const ModalAdmin = ({ open, onClose }) => {
  const [name, setName] = useState('');
  const [pass, setPass] = useState('');

  if (!open) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-admin">
        <div className="modal-admin-icon">
          {/* SVG usuario+engranaje */}
          <svg width="70" height="70" viewBox="0 0 24 24" fill="#6E1F34">
            <circle cx="12" cy="7" r="5" />
            <rect x="3" y="16" width="18" height="5" rx="2.5" />
            <path d="M17.5 12.5l1 1.73-1.5.87v1.74h-2v-1.74l-1.5-.87 1-1.73z" fill="#6E1F34"/>
          </svg>
        </div>
        <h2 className="modal-admin-title">Ingresa contraseña como Administrador</h2>
        <form className="modal-admin-form">
          <label>Nombre *</label>
          <input
            type="text"
            value={name}
            onChange={e => setName(e.target.value)}
            className="modal-admin-input"
          />
          <label>Contraseña *</label>
          <input
            type="password"
            value={pass}
            onChange={e => setPass(e.target.value)}
            className="modal-admin-input"
          />
          <button
            className="modal-admin-btn"
            type="submit"
            onClick={e => {e.preventDefault(); /* integrar login aquí */}}
          >
            Ingresar
          </button>
        </form>
        <button className="modal-admin-close" onClick={onClose}>
          <span>&#10005;</span>
        </button>
      </div>
    </div>
  );
};

export default ModalAdmin;
