import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Scanner } from '@yudiel/react-qr-scanner';
import './AttendanceHome.css';
import AdminLoginModal from './../common/AdminLoginModal';
import SuccessModal from './../common/SuccessModal';

// Variables de entorno o constantes
const ADMIN_USER = 'admin';
const ADMIN_PASS = '1234';

const AttendanceHome = () => {
  const navigate = useNavigate();

  // Estado principal para el registro de asistencia
  const [employeeId, setEmployeeId] = useState('');

  // Estado del modal de administrador
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Estado del modal de éxito
  const [showSuccess, setShowSuccess] = useState(false);

  // Estado para controlar el escáner QR
  const [isScannerActive, setIsScannerActive] = useState(false);
  const [scanError, setScanError] = useState('');

  // Función para registrar asistencia manual (por input)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!employeeId.trim()) {
      alert('Por favor ingrese un ID válido');
      return;
    }

    console.log(`Asistencia registrada para ID: ${employeeId}`);
    setShowSuccess(true);
    setEmployeeId('');

    setTimeout(() => {
      setShowSuccess(false);
    }, 3000);
  };

  // Función para manejar el escaneo exitoso del QR
  const handleQrScan = (result) => {
    if (result && result.length > 0) {
      const scannedData = result[0].rawValue;
      console.log(`QR escaneado: ${scannedData}`);

      // Llenar el input con el ID escaneado
      setEmployeeId(scannedData);

      // Desactivar el escáner
      setIsScannerActive(false);

      // Mostrar modal de éxito
      setShowSuccess(true);

      setTimeout(() => {
        setShowSuccess(false);
      }, 3000);
    }
  };

  // Función para manejar errores del escáner
  const handleScanError = (error) => {
    console.error('Error en el escáner:', error);
    setScanError('Error al acceder a la cámara');
  };

  // Toggle del escáner
  const toggleScanner = () => {
    setIsScannerActive(!isScannerActive);
    setScanError('');
  };

  // Función de login de administrador
  const handleAdminLogin = (username, password) => {
    if (username === ADMIN_USER && password === ADMIN_PASS) {
      setIsModalOpen(false);
      navigate('/employees');
    } else {
      alert('Usuario o contraseña incorrectos');
    }
  };

  return (
    <div className="attendance-home">
      <div className="attendance-container">
        {/* Lado izquierdo - Formulario */}
        <div className="attendance-left">
          <h1 className="attendance-title">Registro de Asistencia</h1>

          <form className="attendance-form" onSubmit={handleSubmit}>
            <input
              type="text"
              className="attendance-input"
              placeholder="Ingrese su ID"
              value={employeeId}
              onChange={(e) => setEmployeeId(e.target.value)}
            />

            <button type="submit" className="attendance-button">
              INGRESAR
            </button>
          </form>

          {/* Botón de configuración de administrador */}
          <div 
            className="attendance-settings-icon"
            onClick={() => setIsModalOpen(true)}
          >
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
              <path d="M12,15.5A3.5,3.5 0 0,1 8.5,12A3.5,3.5 0 0,1 12,8.5A3.5,3.5 0 0,1 15.5,12A3.5,3.5 0 0,1 12,15.5M19.43,12.97C19.47,12.65 19.5,12.33 19.5,12C19.5,11.67 19.47,11.34 19.43,11L21.54,9.37C21.73,9.22 21.78,8.95 21.66,8.73L19.66,5.27C19.54,5.05 19.27,4.96 19.05,5.05L16.56,6.05C16.04,5.66 15.5,5.32 14.87,5.07L14.5,2.42C14.46,2.18 14.25,2 14,2H10C9.75,2 9.54,2.18 9.5,2.42L9.13,5.07C8.5,5.32 7.96,5.66 7.44,6.05L4.95,5.05C4.73,4.96 4.46,5.05 4.34,5.27L2.34,8.73C2.21,8.95 2.27,9.22 2.46,9.37L4.57,11C4.53,11.34 4.5,11.67 4.5,12C4.5,12.33 4.53,12.65 4.57,12.97L2.46,14.63C2.27,14.78 2.21,15.05 2.34,15.27L4.34,18.73C4.46,18.95 4.73,19.03 4.95,18.95L7.44,17.94C7.96,18.34 8.5,18.68 9.13,18.93L9.5,21.58C9.54,21.82 9.75,22 10,22H14C14.25,22 14.46,21.82 14.5,21.58L14.87,18.93C15.5,18.67 16.04,18.34 16.56,17.94L19.05,18.95C19.27,19.03 19.54,18.95 19.66,18.73L21.66,15.27C21.78,15.05 21.73,14.78 21.54,14.63L19.43,12.97Z" />
            </svg>
          </div>
        </div>

        {/* Lado derecho - Escáner QR */}
        <div className="attendance-right">
          <h2 className="qr-title-fixed">ESCANEA CÓDIGO QR</h2>

          <div className="qr-scanner">
            {!isScannerActive ? (
              // Vista del frame decorativo cuando no está escaneando
              <div className="scanner-frame" onClick={toggleScanner}>
                <div className="scanner-corner scanner-tl"></div>
                <div className="scanner-corner scanner-tr"></div>
                <div className="scanner-corner scanner-bl"></div>
                <div className="scanner-corner scanner-br"></div>
                <div className="scanner-dot"></div>
                <div className="scanner-text">
                  Toca para escanear
                </div>
              </div>
            ) : (
              // Vista de la cámara activa
              <div className="scanner-camera-container">
                <Scanner
                  onScan={handleQrScan}
                  onError={handleScanError}
                  constraints={{
                    facingMode: 'environment',
                  }}
                  components={{
                    finder: false,
                  }}
                  styles={{
                    container: {
                      width: '100%',
                      height: '100%',
                      borderRadius: '1.25rem',
                      overflow: 'hidden',
                    },
                    video: {
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                    }
                  }}
                />

                {/* Overlay decorativo sobre la cámara */}
                <div className="scanner-overlay">
                  <div className="scanner-corner scanner-tl"></div>
                  <div className="scanner-corner scanner-tr"></div>
                  <div className="scanner-corner scanner-bl"></div>
                  <div className="scanner-corner scanner-br"></div>

                  {/* Grid de fondo (opcional) */}
                  <div className="scanner-grid"></div>

                  {/* Línea de escaneo principal */}
                  <div className="scanner-line"></div>

                  {/* Texto de estado */}
                  <div className="scanner-status-text">Escaneando...</div>
                </div>

                {/* Botón para cerrar el escáner */}
                <button 
                  className="scanner-close-btn"
                  onClick={toggleScanner}
                >
                  ✕
                </button>

                {scanError && (
                  <div className="scanner-error">{scanError}</div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modal de login de administrador */}
      {isModalOpen && (
        <AdminLoginModal
          onClose={() => setIsModalOpen(false)}
          onLogin={handleAdminLogin}
        />
      )}

      {/* Modal de éxito */}
      {showSuccess && (
        <SuccessModal 
          message={`Asistencia registrada para ID: ${employeeId}`}
          onClose={() => setShowSuccess(false)}
        />
      )}
    </div>
  );
};

export default AttendanceHome;
