import React from 'react';
import './EmployeeList.css';

const employees = [
  { id: 'ADMIN01', name: 'Admin General', area: 'Dirección', role: 'Administrador', active: true, admin: true },
  { id: 'EMP001', name: 'Ana Martínez', area: 'Ciber-escuela', role: 'Docente', active: true },
  { id: 'EMP002', name: 'Carlos Gómez', area: 'Cultura', role: 'Coordinador', active: true },
  { id: 'EMP003', name: 'Lucía Fernández', area: 'Deporte', role: 'Auxiliar', active: false },
];

const EmployeeList = () => {
  return (
    <div className="employee-list">
      <h2>Gestión de Empleados</h2>
      <div className="employee-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Área</th>
              <th>Rol</th>
              <th>Estado</th>
              <th>Tipo</th>
            </tr>
          </thead>
          <tbody>
            {employees.map(emp => (
              <tr key={emp.id} className={emp.admin ? 'admin-row' : ''}>
                <td>{emp.id}</td>
                <td>{emp.name}</td>
                <td>{emp.area}</td>
                <td>{emp.role}</td>
                <td className={emp.active ? 'status-active' : 'status-inactive'}>
                  {emp.active ? 'Activo' : 'Inactivo'}
                </td>
                <td>
                  {emp.admin ? (
                    <span className="badge-admin">Administrador</span>
                  ) : (
                    <span>Empleado</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmployeeList;
