import React from 'react';
import './Button.css';

const Button = ({ 
  children, 
  variant = 'primary', 
  size = 'medium',
  fullWidth = false,
  disabled = false,
  loading = false,
  onClick,
  type = 'button',
  className = ''
}) => {
  const buttonClass = `
    pilares-button 
    button-${variant} 
    button-${size} 
    ${fullWidth ? 'button-full' : ''} 
    ${loading ? 'button-loading' : ''}
    ${className}
  `.trim().replace(/\s+/g, ' ');

  return (
    <button
      type={type}
      className={buttonClass}
      onClick={onClick}
      disabled={disabled || loading}
    >
      {loading ? (
        <>
          <span className="button-spinner"></span>
          <span className="button-text">Cargando...</span>
        </>
      ) : (
        <span className="button-text">{children}</span>
      )}
    </button>
  );
};

export default Button;
