import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './EmployeeList.css';

// Componente para la barra de control (Agregar, Buscar, Reporte)
const ControlPanel = ({ onAddEmployee, onGenerateReport, onSearchChange, searchTerm }) => (
    <div className="control-panel">
        <div className="control-panel-left">
            {/* Bot칩n: Agregar nuevo empleado */}
            <button className="control-button add-button" onClick={onAddEmployee}>
                {/* 칈cono de Persona con signo de m치s (usando un SVG placeholder) */}
                <svg viewBox="0 0 24 24" fill="currentColor">
                    <path d="M16 11c1.66 0 2.99-1.34 2.99-3S17.66 5 16 5c-1.66 0-3 1.34-3 3s1.34 3 3 3zm-8 0c1.66 0 2.99-1.34 2.99-3S9.66 5 8 5C6.34 5 5 6.34 5 8s1.34 3 3 3zm0 2c-2.33 0-6 1.17-6 3.5V19h12v-2.5c0-2.33-3.67-3.5-6-3.5zm8 0c-.29 0-.62.02-.97.08C16.89 15.68 18 16.71 18 18.25V19h4v-2.5c0-2.33-3.67-3.5-6-3.5zM15 15h2v2h-2zm-3 0h2v2h-2z"/>
                </svg>
                Agregar nuevo empleado
            </button>
            
            {/* Bot칩n: Generar Reporte */}
            <button className="control-button report-button" onClick={onGenerateReport}>
                {/* 칈cono de documento (usando un SVG placeholder) */}
                <svg viewBox="0 0 24 24" fill="currentColor">
                    <path d="M14 2H6c-1.1 0-1.99.9-1.99 2L4 20c0 1.1.89 2 1.99 2H18c1.1 0 2-.9 2-2V8l-6-6zm-2 16H6v-2h6v2zm2-5c0 .55-.45 1-1 1h-6c-.55 0-1-.45-1-1v-6c0-.55.45-1 1-1h6c.55 0 1 .45 1 1v6zm-4-4h2v2h-2v-2z"/>
                </svg>
                Generar Reporte
            </button>
        </div>

        <div className="control-panel-right">
            {/* Campo de b칰squeda */}
            <input
                type="text"
                className="search-input"
                placeholder="Buscar por Nombre o ID"
                value={searchTerm}
                onChange={(e) => onSearchChange(e.target.value)}
            />
            {/* Bot칩n de b칰squeda (opcional, ya que se puede buscar al teclear) */}
            <button className="control-button search-button">Buscar</button>
        </div>
    </div>
);


const EmployeeList = () => {
    const navigate = useNavigate();
    const initialEmployees = [
        { id: 'EMP001', name: 'Ana Mart칤nez', area: 'Ciber-escuela', role: 'Docente', active: true },
        { id: 'EMP002', name: 'Carlos G칩mez', area: 'Cultura', role: 'Coordinador', active: true },
        { id: 'EMP003', name: 'Luc칤a Fern치ndez', area: 'Deporte', role: 'Auxiliar', active: false },
        { id: 'EMP004', name: 'Miguel P칠rez', area: 'Ciber-escuela', role: 'Docente', active: true },
        // ... puedes agregar m치s datos aqu칤
    ];
    
    // Estado para la b칰squeda y los empleados filtrados
    const [searchTerm, setSearchTerm] = useState('');
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);

    const [newEmployeeData, setNewEmployeeData] = useState({
        fullName: '',
        employeeId: '',
        email: '',
        phone: '',
        position: '',
        area: '',
        // Aqu칤 ir칤an los estados para las horas
    });

    // ----------------------------------------------------
    // L칩gica de filtrado de empleados
    const filteredEmployees = initialEmployees.filter(emp => 
        emp.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
        emp.id.toLowerCase().includes(searchTerm.toLowerCase())
    );
    // ----------------------------------------------------

    // Funciones de acci칩n
    const handleAddEmployee = () => {
        alert('Acci칩n: Abrir modal o formulario para agregar nuevo empleado.');
        // Aqu칤 implementar칤as la l칩gica para abrir un modal de formulario
    };

    const handleGenerateReport = () => {
        navigate('/reports');
    };


    return (
        <div className="employee-list">
            <h2>Gesti칩n de Empleados</h2>
            
            {/* 游녣 INSERTAMOS EL PANEL DE CONTROL AQU칈 */}
            <ControlPanel
                onAddEmployee={handleAddEmployee}
                onGenerateReport={handleGenerateReport}
                onSearchChange={setSearchTerm}
                searchTerm={searchTerm}
            />
            
            <div className="employee-table">
                {/* Si hay resultados, muestra la tabla */}
                {filteredEmployees.length > 0 ? (
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Nombre</th>
                                <th>츼rea</th>
                                <th>Rol</th>
                                <th>Estado</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredEmployees.map(emp => (
                                <tr key={emp.id}>
                                    <td>{emp.id}</td>
                                    <td>{emp.name}</td>
                                    <td>{emp.area}</td>
                                    <td>{emp.role}</td>
                                    <td className={emp.active ? 'status-active' : 'status-inactive'}>
                                        {emp.active ? 'Activo' : 'Inactivo'}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    // Mensaje si no hay resultados
                    <p className="no-results">No se encontraron empleados que coincidan con "{searchTerm}".</p>
                )}
            </div>
        </div>
    );
};

export default EmployeeList;
