import React, { useState } from 'react';
import './AttendanceHome.css';
import AttendanceForm from './AttendanceForm';
import QRScanner from './QRScanner';
import ConfirmationModal from './ConfirmationModal';
import LoadingState from './LoadingState';

const AttendanceHome = () => {
  const [loading, setLoading] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [attendanceType, setAttendanceType] = useState(''); // 'entrada' or 'salida'
  const [employeeData, setEmployeeData] = useState(null);

  const handleAttendanceSubmit = async (data) => {
    setLoading(true);

    // Simular llamada a API
    setTimeout(() => {
      setEmployeeData(data);
      setAttendanceType(data.type);
      setLoading(false);
      setShowConfirmation(true);
    }, 2000);
  };

  const handleCloseConfirmation = () => {
    setShowConfirmation(false);
    setEmployeeData(null);
  };

  return (
    <div className="attendance-home">
      <div className="attendance-container">
        <div className="attendance-left">
          <h2 className="attendance-title">Registro de Asistencia</h2>
          {loading ? (
            <LoadingState />
          ) : (
            <AttendanceForm onSubmit={handleAttendanceSubmit} />
          )}
        </div>

        <div className="attendance-right">
          <h2 className="qr-title">ESCANEA CODIGO QR</h2>
          <QRScanner />
        </div>
      </div>

      {showConfirmation && (
        <ConfirmationModal
          isOpen={showConfirmation}
          onClose={handleCloseConfirmation}
          type={attendanceType}
          employeeData={employeeData}
        />
      )}
    </div>
  );
};

export default AttendanceHome;
