import React from 'react';
import './ConfirmationModal.css';
import Modal from '../common/Modal';
import Button from '../common/Button';

const ConfirmationModal = ({ isOpen, onClose, type, employeeData }) => {
  const getMessage = () => {
    if (type === 'entrada') {
      return {
        title: '¡Entrada registrada!',
        message: '¡Buen día, tu asistencia ha sido marcada!',
        subtitle: 'Tu registro de entrada fue guardado correctamente.'
      };
    } else {
      return {
        title: '¡Salida registrada!',
        message: 'Tu jornada ha finalizado. ¡Hasta pronto!',
        subtitle: 'Tu registro de salida fue guardado correctamente.'
      };
    }
  };

  const { title, message, subtitle } = getMessage();

  return (
    <Modal isOpen={isOpen} onClose={onClose} size="medium">
      <div className="confirmation-content">
        <div className="confirmation-icon">
          <svg width="64" height="64" viewBox="0 0 64 64">
            <circle cx="32" cy="32" r="30" fill="#0F883B" />
            <path d="M20 32 L28 40 L44 24" stroke="#FFFFFF" strokeWidth="4" fill="none" strokeLinecap="round" strokeLinejoin="round" />
          </svg>
        </div>

        <h3 className="confirmation-title">{title}</h3>
        <p className="confirmation-message">{message}</p>
        <p className="confirmation-subtitle">{subtitle}</p>

        {employeeData && (
          <div className="employee-info">
            <p><strong>ID:</strong> {employeeData.employeeId}</p>
            <p><strong>Hora:</strong> {new Date(employeeData.timestamp).toLocaleTimeString('es-MX')}</p>
          </div>
        )}

        <Button variant="success" size="large" fullWidth onClick={onClose}>
          ACEPTAR
        </Button>
      </div>
    </Modal>
  );
};

export default ConfirmationModal;
